    public boolean validatePassword(String password) {
        Pattern passwordRegex = Pattern.compile("(^.*(?=.{8,})(?=.*\\d)(?=.*[a-zA-Z])|(?=.{8,})(?=.*\\d)(?=.*[!@#$%^&])|(?=.{8,})(?=.*[a-zA-Z])(?=.*[!@#$%^&]).*$)");
        if (!passwordRegex.matcher(password).matches()) {
            System.out.println("Please provide valid password containing: \n - a digit must occur at least once \n - a lower case letter must occur at least once \n - an upper case letter must occur at least once \n - a special character must occur at least once \n - no whitespace allowed in the entire string \n - at least 8 characters");
            return false;
        }
        return true;
    }

    private void printUsers(List<User> users) {
        for (User user :
                users) {
            System.out.println("id:" + user.getId() + " email:" + user.getEmail() + " password:" + user.getPassword());
        }
    }
